    if (!newJob.jobNumber || !newJob.partNumber || !newJob.dueDate) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields (Job Number, Part Number, Due Date).",
        variant: "destructive",
      });
      return;
    }
    createJobMutation.mutate(newJob);
  };

