                    <div className="grid grid-cols-2 gap-2">
                      {groupMachines.map((machine: any) => (
                        <div key={machine.id} className="flex items-center justify-between p-2 bg-gray-50 dark:bg-gray-700 rounded">
                          <span className="text-sm">{machine.machineId} - {machine.name}</span>
                          <Badge variant={machine.status === 'Available' ? 'default' : 'secondary'}>
                            {machine.status}
                          </Badge>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>

              <div className="flex justify-end space-x-2 pt-4">
                <Button variant="outline" onClick={() => setIsRoutingDialogOpen(false)}>
                  Cancel
                </Button>
                <Button onClick={() => {
                  toast({
                    title: "Routing Updated",
                    description: "Job routing has been adjusted based on selected machine groups.",
                  });
                  setIsRoutingDialogOpen(false);
                }}>
                  Apply Routing Changes
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
        </CollapsibleContent>
      </Card>

      {/* Manual Scheduling Dialog */}
      <Dialog open={isManualScheduleOpen} onOpenChange={setIsManualScheduleOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Manual Schedule Job</DialogTitle>
          </DialogHeader>
          {manualScheduleJob && (
            <div className="space-y-4 py-4">
              <div className="space-y-2">
                <p className="text-sm text-gray-600">
                  <strong>Job:</strong> {manualScheduleJob.jobNumber} - {manualScheduleJob.partNumber}
                </p>
                <p className="text-sm text-gray-600">
                  <strong>Description:</strong> {manualScheduleJob.description}
                </p>
                <p className="text-sm text-gray-600">
                  <strong>Due Date:</strong> {new Date(manualScheduleJob.dueDate).toLocaleDateString()}
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="manualStartDate">Start Date for First Operation</Label>
                <Input
                  id="manualStartDate"
                  type="date"
                  value={manualStartDate}
                  onChange={(e) => setManualStartDate(e.target.value)}
                  min={new Date().toISOString().split('T')[0]}
                  data-testid="input-manual-start-date"
                />
                <p className="text-xs text-gray-500">
                  Following operations will be scheduled automatically based on this start date.
                </p>
              </div>

              <div className="flex justify-between pt-4">
                <Button 
                  variant="outline" 
                  onClick={() => setIsManualScheduleOpen(false)}
                  data-testid="button-cancel-manual-schedule"
                >
                  Cancel
                </Button>
                <Button 
                  onClick={() => {
                    if (!manualStartDate) {
                      toast({
                        title: "Missing Start Date",
                        description: "Please select a start date for the first operation.",
                        variant: "destructive",
                      });
                      return;
                    }
                    manualScheduleMutation.mutate({
                      jobId: manualScheduleJob.id,
                      startDate: manualStartDate
                    });
                  }}
                  disabled={manualScheduleMutation.isPending || !manualStartDate}
                  data-testid="button-confirm-manual-schedule"
                >
                  {manualScheduleMutation.isPending ? 'Scheduling...' : 'Schedule Job'}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Progress tracking for auto-scheduling */}
      <ScheduleProgressToast
        isVisible={isScheduleProgressVisible}
        onClose={() => setIsScheduleProgressVisible(false)}
      />
    </Collapsible>
  );
}

client/src/components/jobs-awaiting-mate.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Clock, AlertTriangle, Package2, Trash2 } from "lucide-react";
import { Link } from "wouter";
import CollapsibleCard from "@/components/collapsible-card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Job, MaterialOrder } from "@shared/schema";

interface JobWithMaterials extends Job {
  materialOrders: MaterialOrder[];
}

export default function JobsAwaitingMaterialWidget() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: jobsAwaitingMaterial, isLoading } = useQuery<JobWithMaterials[]>({
    queryKey: ['/api/jobs/awaiting-material'],
  });

  const deleteAllMutation = useMutation({
    mutationFn: () => apiRequest('/api/jobs/awaiting-material/all', 'DELETE'),
    onSuccess: (data: { deletedCount: number }) => {
      queryClient.invalidateQueries({ queryKey: ['/api/jobs/awaiting-material'] });
      queryClient.invalidateQueries({ queryKey: ['/api/material-orders'] });
      queryClient.invalidateQueries({ queryKey: ['/api/jobs'] });
      toast({
        title: "Material dependencies cleared",
        description: `Cleared material dependencies for ${data.deletedCount} orders.`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to clear material dependencies.",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <CollapsibleCard
        title="Jobs Awaiting Material"
        icon={<Clock className="h-4 w-4 text-muted-foreground" />}
      >
        <div className="animate-pulse space-y-2">
          <div className="h-4 bg-gray-200 rounded"></div>
          <div className="h-4 bg-gray-200 rounded"></div>
          <div className="h-4 bg-gray-200 rounded"></div>
        </div>
      </CollapsibleCard>
    );
  }

  const awaitingJobs = jobsAwaitingMaterial || [];
  const criticalJobs = awaitingJobs.filter(job => {
    const dueDate = new Date(job.dueDate);
    const today = new Date();
    const daysDiff = Math.ceil((dueDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
    return daysDiff <= 7; // Jobs due within a week
  });

  const formatDate = (date: Date | string) => {
    const dateObj = typeof date === 'string' ? new Date(date) : date;
    return dateObj.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric'
    });
  };

  const getDaysToDue = (dueDate: Date | string) => {
    const dateObj = typeof dueDate === 'string' ? new Date(dueDate) : dueDate;
    const today = new Date();
    const diffTime = dateObj.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  return (
    <CollapsibleCard
      title="Jobs Awaiting Material"
      icon={<Package2 className="h-4 w-4 text-muted-foreground" />}
      headerActions={
        <div className="flex items-center space-x-1">
          {awaitingJobs.length > 0 && (
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={() => deleteAllMutation.mutate()}
              disabled={deleteAllMutation.isPending}
              data-testid="delete-all-jobs-awaiting-material"
            >
              <Trash2 className="h-3 w-3" />
            </Button>
          )}
          <Link href="/materials">
            <Button variant="ghost" size="sm" className="text-xs">
              Manage
            </Button>
          </Link>
        </div>
      }
    >
        <div className="space-y-3">
          {/* Summary Stats */}
          <div className="flex justify-between items-center">
            <div className="text-2xl font-bold">{awaitingJobs.length}</div>
            <div className="text-xs text-muted-foreground">Jobs Blocked</div>
          </div>

          {criticalJobs.length > 0 && (
            <div className="flex items-center space-x-2 p-2 bg-orange-50 rounded-lg">
              <AlertTriangle className="h-4 w-4 text-orange-500" />
              <span className="text-sm text-orange-700">
                {criticalJobs.length} job{criticalJobs.length > 1 ? 's' : ''} due soon
              </span>
            </div>
          )}

          {/* Job List */}
          <div className="space-y-2">
            <div className="text-xs font-medium text-muted-foreground">Recent Jobs</div>
            {awaitingJobs.slice(0, 3).map((job) => {
              const daysToDue = getDaysToDue(job.dueDate);
              const isCritical = daysToDue <= 7;
              const pendingMaterials = job.materialOrders.filter(order => order.status !== 'Received').length;

              return (
                <div key={job.id} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-medium truncate">
                      Job {job.jobId}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {pendingMaterials} material{pendingMaterials > 1 ? 's' : ''} pending
                    </div>
                  </div>
                  <div className="flex items-center space-x-2 ml-2">
                    {isCritical ? (
                      <Badge variant="destructive" className="text-xs">
                        {daysToDue > 0 ? `${daysToDue}d left` : 'Overdue'}
                      </Badge>
                    ) : (
                      <Badge variant="outline" className="text-xs">
                        Due {formatDate(job.dueDate)}
                      </Badge>
                    )}
                    <Clock className="h-3 w-3 text-muted-foreground" />
                  </div>
                </div>
              );
            })}

            {awaitingJobs.length === 0 && (
              <div className="text-center py-4 text-sm text-muted-foreground">
                All jobs ready for scheduling
              </div>
            )}

            {awaitingJobs.length > 3 && (
              <Link href="/materials">
                <Button variant="ghost" size="sm" className="w-full mt-2">
                  View all {awaitingJobs.length} blocked jobs
                </Button>
              </Link>
            )}
          </div>
        </div>
    </CollapsibleCard>
  );
}

client/src/components/machine-status.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Settings, Cpu } from "lucide-react";
import CollapsibleCard from "@/components/collapsible-card";
import type { Machine } from "@shared/schema";

interface MachineStatusProps {
  minimized?: boolean;
}

export default function MachineStatus({ minimized = false }: MachineStatusProps) {
  const { data: machines, isLoading, error } = useQuery<Machine[]>({
    queryKey: ['/api/machines'],
  });



  if (isLoading) {
    return (
      <CollapsibleCard
        title="Work Center Status"
        icon={<Settings className="h-4 w-4 text-muted-foreground" />}
      >
        <div className="animate-pulse space-y-4">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="h-16 bg-gray-200 rounded"></div>
          ))}
        </div>
      </CollapsibleCard>
    );
  }

  if (!machines || machines.length === 0) {
    return (
      <CollapsibleCard
        title="Work Center Status"
        icon={<Settings className="h-4 w-4 text-muted-foreground" />}
      >
        <div className="text-center text-gray-500 py-8">
          No work centers configured
        </div>
      </CollapsibleCard>
    );
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Available':
        return 'bg-success-500';
      case 'Busy':
        return 'bg-warning-500';
      case 'Maintenance':
        return 'bg-error-500';
      case 'Offline':
        return 'bg-gray-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getTierColor = (tier: string) => {
    switch (tier) {
      case 'Premium':
        return 'text-blue-600 bg-blue-100';
      case 'Standard':
        return 'text-green-600 bg-green-100';
      case 'Budget':
        return 'text-orange-600 bg-orange-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (machine: Machine) => {
    return machine.status;
  };

  const getStatusSubtext = (machine: Machine) => {
    return machine.category || '';
  };

  // Group machines by type and sort both groups and machines alphanumerically
  const groupedMachines = machines?.reduce((groups, machine) => {
    const key = machine.type;
    if (!groups[key]) {
      groups[key] = [];
    }
    groups[key].push(machine);
    return groups;
  }, {} as Record<string, Machine[]>) || {};

  // Sort machine types alphabetically and machines within each type by machineId
  const sortedGroupedMachines = Object.keys(groupedMachines)
    .sort()
    .reduce((sorted, type) => {
      sorted[type] = groupedMachines[type].sort((a, b) => a.machineId.localeCompare(b.machineId));
      return sorted;
    }, {} as Record<string, Machine[]>);

  return (
    <CollapsibleCard
      title="Work Center Status"
      icon={<Cpu className="h-4 w-4 text-blue-500" />}
      defaultCollapsed={minimized}
      className={minimized ? "opacity-75 text-xs" : ""}
    >
      <div className="space-y-6">
        {Object.entries(sortedGroupedMachines).map(([type, typeMachines]) => (
          <div key={type} className="space-y-3">
            <h3 className="text-sm font-semibold text-gray-800 border-b border-gray-200 pb-1">
              {type}
            </h3>
            <div className="space-y-2">
              {typeMachines.map((machine) => (
                <div key={machine.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center space-x-3">
                    <div className={`w-3 h-3 ${getStatusColor(machine.status)} rounded-full`}></div>
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <div className="text-sm font-medium text-gray-900">{machine.machineId}</div>
                        <span className={`px-2 py-1 text-xs rounded-full font-medium ${getTierColor(machine.tier)}`}>
                          {machine.tier}
                        </span>
                      </div>
                      <div className="text-xs text-gray-500">{machine.name}</div>
                      {machine.category && (
                        <div className="text-xs text-blue-600">{machine.category}</div>
                      )}
                      {machine.substitutionGroup && (
                        <div className="text-xs text-purple-600">Group: {machine.substitutionGroup}</div>
                      )}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className={`text-sm ${machine.status === 'Offline' || machine.status === 'Maintenance' ? 'text-error-600' : 'text-gray-900'}`}>
                      {getStatusText(machine)}
                    </div>
                    <div className="text-xs text-gray-500">{getStatusSubtext(machine)}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </CollapsibleCard>
  );
}

