import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { TrendingDown, TrendingUp, AlertTriangle, CheckCircle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";

interface EfficiencyData {
  totalOperations: number;
  substitutedOperations: number;
  averageEfficiencyImpact: number;
  worstImpacts: Array<{
    jobNumber: string;
    operationName: string;
    originalMachine: string;
    assignedMachine: string;
    efficiencyImpact: number;
  }>;
}

export function EfficiencyImpactWidget() {
  const { data: efficiencyData, isLoading } = useQuery<EfficiencyData>({
    queryKey: ['/api/efficiency-impact'],
    refetchInterval: 30000,
  });

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingDown className="h-5 w-5" />
            Efficiency Impact
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-muted-foreground">Loading efficiency data...</div>
        </CardContent>
      </Card>
    );
  }

  if (!efficiencyData) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingDown className="h-5 w-5" />
            Efficiency Impact
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-muted-foreground">No efficiency data available</div>
        </CardContent>
      </Card>
    );
  }

  const substitutionRate = efficiencyData.totalOperations > 0 
    ? (efficiencyData.substitutedOperations / efficiencyData.totalOperations) * 100 
    : 0;

  const getImpactColor = (impact: number) => {
    if (impact > 20) return "bg-red-100 text-red-800";
    if (impact > 10) return "bg-orange-100 text-orange-800";
    if (impact > 0) return "bg-yellow-100 text-yellow-800";
    if (impact < -10) return "bg-green-100 text-green-800";
    return "bg-gray-100 text-gray-800";
  };

  const getImpactIcon = (impact: number) => {
    if (impact > 10) return <AlertTriangle className="h-4 w-4" />;
    if (impact > 0) return <TrendingUp className="h-4 w-4" />;
    if (impact < 0) return <TrendingDown className="h-4 w-4" />;
    return <CheckCircle className="h-4 w-4" />;
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <TrendingDown className="h-5 w-5" />
          Efficiency Impact
        </CardTitle>
        <CardDescription>
          Impact from machine substitutions
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Overall Stats */}
        <div className="grid grid-cols-2 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold">{substitutionRate.toFixed(1)}%</div>
            <div className="text-sm text-muted-foreground">Substitution Rate</div>
          </div>
          <div className="text-center">
            <div className={`text-2xl font-bold ${efficiencyData.averageEfficiencyImpact > 0 ? 'text-red-600' : 'text-green-600'}`}>
              {efficiencyData.averageEfficiencyImpact > 0 ? '+' : ''}{efficiencyData.averageEfficiencyImpact.toFixed(1)}%
            </div>
            <div className="text-sm text-muted-foreground">Avg Impact</div>
          </div>
        </div>

        {/* Worst Impacts */}
        {efficiencyData.worstImpacts.length > 0 && (
          <div>
            <h4 className="text-sm font-medium mb-2">Significant Impacts</h4>
            <div className="space-y-2">
              {efficiencyData.worstImpacts.slice(0, 3).map((impact, index) => (
                <div key={index} className="flex items-center justify-between p-2 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-medium truncate">
                      {impact.jobNumber} - {impact.operationName}
                    </div>
                    <div className="text-xs text-muted-foreground truncate">
                      {impact.originalMachine} ‚Üí {impact.assignedMachine}
                    </div>
                  </div>
                  <Badge 
                    variant="outline" 
                    className={`flex items-center gap-1 ${getImpactColor(impact.efficiencyImpact)}`}
                  >
                    {getImpactIcon(impact.efficiencyImpact)}
                    +{impact.efficiencyImpact.toFixed(1)}%
                  </Badge>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Status Summary */}
        <div className="text-xs text-muted-foreground">
          {efficiencyData.substitutedOperations} of {efficiencyData.totalOperations} scheduled operations use substitute machines
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/schedule-progress-.content:
import { useState, useEffect } from "react";
import { Progress } from "@/components/ui/progress";
import { Card, CardContent } from "@/components/ui/card";
import { X, CheckCircle, AlertCircle, Clock } from "lucide-react";
import { Button } from "@/components/ui/button";

interface ScheduleProgressToastProps {
  isVisible: boolean;
  onClose: () => void;
}

interface ProgressData {
  jobId: string;
  progress: number;
  status: string;
  stage: string;
  operationName?: string;
  currentOperation?: number;
  totalOperations?: number;
}

export default function ScheduleProgressToast({ isVisible, onClose }: ScheduleProgressToastProps) {
  const [progressData, setProgressData] = useState<ProgressData | null>(null);
  const [isComplete, setIsComplete] = useState(false);
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    if (!isVisible) return;

    const ws = new WebSocket(`${window.location.protocol === 'https:' ? 'wss:' : 'ws:'}//${window.location.host}/ws`);

    ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);

        if (message.type === 'schedule_progress') {
          const data = message.data as ProgressData;
          setProgressData(data);

          if (data.stage === 'completed') {
            setIsComplete(true);
            setTimeout(() => {
              onClose();
            }, 3000); // Auto-close after 3 seconds when complete
          } else if (data.stage === 'error') {
            setHasError(true);
            setTimeout(() => {
              onClose();
            }, 5000); // Auto-close after 5 seconds on error
          }
        }
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    return () => {
      ws.close();
    };
  }, [isVisible, onClose]);

  if (!isVisible || !progressData) {
    return null;
  }

  const getStageIcon = () => {
    if (hasError) return <AlertCircle className="h-5 w-5 text-red-500" />;
    if (isComplete) return <CheckCircle className="h-5 w-5 text-green-500" />;
    return <Clock className="h-5 w-5 text-blue-500 animate-spin" />;
  };

  const getStageColor = () => {
    if (hasError) return "border-red-200 bg-red-50";
    if (isComplete) return "border-green-200 bg-green-50";
    return "border-blue-200 bg-blue-50";
  };

  return (
    <div className="fixed bottom-4 right-4 z-50 max-w-md">
      <Card className={`shadow-lg ${getStageColor()}`}>
        <CardContent className="p-4">
          <div className="flex items-start justify-between mb-3">
            <div className="flex items-center space-x-2">
              {getStageIcon()}
              <h3 className="font-semibold text-sm">Auto-Schedule Progress</h3>
            </div>
            <Button
              variant="ghost"
              size="sm"
              className="h-6 w-6 p-0"
              onClick={onClose}
              data-testid="button-close-progress"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>

          <div className="space-y-2">
            <div className="flex justify-between text-xs text-muted-foreground">
              <span>
                {progressData.currentOperation && progressData.totalOperations
                  ? `Operation ${progressData.currentOperation} of ${progressData.totalOperations}`
                  : 'Processing...'
                }
              </span>
              <span>{progressData.progress}%</span>
            </div>

            <Progress 
              value={progressData.progress} 
              className="h-2"
              data-testid="progress-bar"
            />

            <div className="text-sm">
              <p className="font-medium">{progressData.status}</p>
              {progressData.operationName && (
                <p className="text-xs text-muted-foreground mt-1">
                  Current: {progressData.operationName}
                </p>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

